CREATE TABLE IF NOT EXISTS menu (
    `Item_id` INT,
    `Item_Category` VARCHAR(20) CHARACTER SET utf8,
    `Item_subcategory` VARCHAR(14) CHARACTER SET utf8,
    `Item_name1` VARCHAR(22) CHARACTER SET utf8,
    `Item_name2` VARCHAR(22) CHARACTER SET utf8,
    `Item_price` INT,
    `Item_Veg` VARCHAR(7) CHARACTER SET utf8,
    `Item_image` VARCHAR(61) CHARACTER SET utf8,
    `Item_Remarks` VARCHAR(21) CHARACTER SET utf8
);
